(function() {
    var amp = String.fromCharCode(38);
    var maxResults = 6;
    var params = new URLSearchParams(window.location.search);
    var currentPage = parseInt(params.get('page')) || 1;
    var grid = document.getElementById('posts-grid');
    var cache = {};
    var newerLink = document.getElementById('newer-link');
    var olderLink = document.getElementById('older-link');

    // Detect context: homepage, label, or search
    var path = window.location.pathname;
    var isLabelPage = path.indexOf('/search/label/') === 0;
    var isSearchPage = path === '/search' && params.has('q');
    var labelName = isLabelPage ? decodeURIComponent(path.split('/search/label/')[1]) : null;

    // Helper to enable/disable buttons (MD3 on-surface-variant for contrast)
    function enableBtn(btn) { btn.style.color = '#202124'; btn.style.cursor = 'pointer'; }
    function disableBtn(btn) { btn.style.color = '#757575'; btn.style.cursor = 'default'; btn.onclick = null; }

    // Helper for status bar management
    function getOrCreateStatusBar() {
        var bar = document.querySelector('.search-status-wrap');
        if (!bar) {
            bar = document.createElement('div');
            bar.className = 'search-status-wrap';
            bar.innerHTML = '<div class="status-text"></div>';
            bar.style.display = 'none';
            grid.parentNode.insertBefore(bar, grid);
        }
        return bar;
    }
    function showStatus(labelText) {
        var bar = getOrCreateStatusBar();
        bar.style.display = 'block';
        bar.querySelector('.status-text').innerHTML = 'Menampilkan artikel dengan label <span class="status-highlight">' + labelText + '</span>';
    }
    function hideStatus() {
        var bar = document.querySelector('.search-status-wrap');
        if (bar) bar.style.display = 'none';
    }

    // For search pages, use Blogger's native URLs (passed from SSR)
    if (isSearchPage) {
        // Buttons stay disabled since search pagination uses Blogger's timestamps
        // which don't have a JSON feed equivalent
        return;
    }

    // Render function
    function renderPosts(entries) {
        grid.innerHTML = '';
        entries.forEach(function(entry) {
            var url = entry.link.find(function(l) { return l.rel === 'alternate'; }).href;
            var title = entry.title.$t;
            var snippet = '';
            if (entry.content && entry.content.$t) {
                var tmp = document.createElement('div');
                tmp.innerHTML = entry.content.$t;
                snippet = tmp.textContent.substring(0, 140) + '...';
            }
            var label = entry.category ? entry.category[0].term : 'Article';
            var labelUrl = '/search/label/' + encodeURIComponent(label);
            var img = entry.media$thumbnail ? entry.media$thumbnail.url.replace('/s72-c/', '/w600/') : '';

            var card = document.createElement('article');
            card.className = 'post-card';
            card.innerHTML = 
                '<a href="' + url + '" aria-label="' + title + '" style="display:block;text-decoration:none;color:inherit">' +
                '<div class="card-media">' + (img ? '<img alt="' + title + '" src="' + img + '" loading="lazy"/>' : '<div style="width:100%;height:100%;background:#f1f3f4;display:flex;align-items:center;justify-content:center;color:#bdc1c6;"><svg style="width:48px" viewBox="0 0 24 24"><path d="M18 13v7H4V6h5.02c.05-.71.22-1.38.48-2H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2v-5l-2-2zm-1.5 5h-11l3.5-4.5 2.5 3.01L14.5 12l2 2.68z" fill="currentColor"></path></svg></div>') + '</div></a>' +
                '<div class="card-meta"><a href="' + labelUrl + '" style="text-decoration:none;color:inherit">' + label + '</a></div>' +
                '<a href="' + url + '" style="text-decoration:none;color:inherit"><h2 class="card-title">' + title + '</h2></a>' +
                '<div class="card-snippet">' + snippet + '</div>' +
                '<a href="' + url + '" style="text-decoration:none;margin-top:auto;width:fit-content"><div class="read-more-pill" style="margin-top:0">Read Article â†’</div></a>';
            grid.appendChild(card);
        });
        window.scrollTo({top: 0, behavior: 'smooth'});
    }

    // Fetch page data (context-aware)
    function fetchPage(page) {
        var start = (page - 1) * maxResults + 1;
        var feedUrl;
        if (isLabelPage && labelName) {
            // Label feed: /feeds/posts/default/-/LabelName
            feedUrl = '/feeds/posts/default/-/' + encodeURIComponent(labelName) + '?alt=json' + amp + 'start-index=' + start + amp + 'max-results=' + maxResults;
        } else {
            // Default feed
            feedUrl = '/feeds/posts/default?alt=json' + amp + 'start-index=' + start + amp + 'max-results=' + maxResults;
        }
        return fetch(feedUrl).then(function(r) { return r.json(); });
    }

    // Prefetch and cache
    function prefetch(page) {
        if (cache[page]) return;
        fetchPage(page).then(function(data) {
            if (data.feed.entry) cache[page] = data.feed.entry;
        });
    }

    // Navigate to page instantly
    function goToPage(page, pushState) {
        currentPage = page;
        var baseUrl = isLabelPage ? path : '/';
        var newUrl = page === 1 ? baseUrl : baseUrl + '?page=' + page;
        if (pushState) history.pushState({page: page}, '', newUrl);

        // Update buttons (will be re-checked after render)
        updateButtons();

        // If page 1, reload to get SSR content
        if (page === 1) { window.location.href = baseUrl; return; }

        // If cached, render instantly
        if (cache[page]) {
            renderPosts(cache[page]);
            checkOlderState(); // Check button state for new page
        } else {
            // Show minimal loading, fetch, render
            grid.style.opacity = '0.5';
            fetchPage(page).then(function(data) {
                if (data.feed.entry) {
                    cache[page] = data.feed.entry;
                    renderPosts(data.feed.entry);
                    checkOlderState(); // Check button state for new page
                }
                grid.style.opacity = '1';
            });
        }
    }

    // Update button states
    function updateButtons() {
        // Newer button
        if (currentPage > 1) {
            enableBtn(newerLink);
            newerLink.onclick = function(e) { e.preventDefault(); goToPage(currentPage - 1, true); };
        } else {
            disableBtn(newerLink);
        }

        // Older button - always disabled initially, enabled by idle check
        disableBtn(olderLink);
    }

    // Initial load for page 2+
    if (currentPage > 1) {
        grid.style.opacity = '0.5';
        fetchPage(currentPage).then(function(data) {
            if (data.feed.entry) {
                cache[currentPage] = data.feed.entry;
                renderPosts(data.feed.entry);
            }
            grid.style.opacity = '1';
        });
    }

    // Check for next page and enable Older button if posts exist
    function checkOlderState() {
        disableBtn(olderLink);
        fetchPage(currentPage + 1).then(function(data) {
            if (data.feed && data.feed.entry && data.feed.entry.length > 0) {
                cache[currentPage + 1] = data.feed.entry;
                enableBtn(olderLink);
                olderLink.onclick = function(e) { e.preventDefault(); goToPage(currentPage + 1, true); };
            }
        }).catch(function(err) {
            console.error('checkOlderState error:', err);
        });
    }

    // Setup buttons
    updateButtons();

    // Check for next page - run immediately on homepage too
    setTimeout(checkOlderState, 100);

    // ========== AJAX LABEL NAVIGATION ==========
    // Intercept label link clicks for SPA-like navigation
    document.addEventListener('click', function(e) {
        var link = e.target.closest('a[href*="/search/label/"]');
        if (!link) return;

        // Only intercept if we're on homepage or label page (not single post)
        var currentPath = window.location.pathname;
        var isValidContext = currentPath === '/' || currentPath.indexOf('/search/label/') === 0;
        if (!isValidContext) return;

        e.preventDefault();

        // Extract label from clicked link
        var href = link.getAttribute('href');
        var clickedLabel = decodeURIComponent(href.split('/search/label/')[1]);

        // Update state
        labelName = clickedLabel;
        isLabelPage = true;
        currentPage = 1;
        cache = {}; // Clear cache for new label

        // Update URL
        history.pushState({label: clickedLabel, page: 1}, '', href);

        // Update/show status bar
        showStatus(clickedLabel);

        // Quick fade for perceived speed
        grid.style.opacity = '0.7';
        grid.style.transition = 'opacity 0.15s';
        fetchPage(1).then(function(data) {
            if (data.feed.entry) {
                cache[1] = data.feed.entry;
                renderPosts(data.feed.entry);
                updateButtons();
                checkOlderState();
            } else {
                grid.innerHTML = '<p style="text-align:center;color:#5F6368;padding:40px;">Tidak ada artikel dengan label ini.</p>';
            }
            grid.style.opacity = '1';
        });
    });

    // ========== AJAX LOGO/HOMEPAGE NAVIGATION ==========
    document.addEventListener('click', function(e) {
        var logoLink = e.target.closest('.brand-link');
        if (!logoLink) return;

        // Only intercept if NOT already on homepage
        var currentPath = window.location.pathname;
        if (currentPath === '/') return; // Let normal behavior happen

        // Only intercept from label pages
        if (currentPath.indexOf('/search/label/') !== 0) return;

        e.preventDefault();

        // Reset state to homepage
        labelName = null;
        isLabelPage = false;
        currentPage = 1;
        cache = {};

        // Update URL
        history.pushState({home: true, page: 1}, '', '/');

        // Hide status bar
        hideStatus();

        // Fetch homepage posts
        grid.style.opacity = '0.7';
        grid.style.transition = 'opacity 0.15s';
        fetchPage(1).then(function(data) {
            if (data.feed.entry) {
                cache[1] = data.feed.entry;
                renderPosts(data.feed.entry);
                updateButtons();
                checkOlderState();
            }
            grid.style.opacity = '1';
        });
    });

    // Handle popstate for all navigation
    window.onpopstate = function(e) {
        if (e.state && e.state.label) {
            // Label state
            labelName = e.state.label;
            isLabelPage = true;
            currentPage = e.state.page || 1;
            cache = {};
            showStatus(e.state.label);
            fetchPage(currentPage).then(function(data) {
                if (data.feed.entry) {
                    cache[currentPage] = data.feed.entry;
                    renderPosts(data.feed.entry);
                    updateButtons();
                    checkOlderState();
                }
            });
        } else if (e.state && e.state.home) {
            // Homepage state
            labelName = null;
            isLabelPage = false;
            currentPage = e.state.page || 1;
            cache = {};
            hideStatus();
            fetchPage(currentPage).then(function(data) {
                if (data.feed.entry) {
                    cache[currentPage] = data.feed.entry;
                    renderPosts(data.feed.entry);
                    updateButtons();
                    checkOlderState();
                }
            });
        } else if (e.state && e.state.page) {
            goToPage(e.state.page, false);
        } else {
            // No state - reload to be safe
            window.location.reload();
        }
    };
})();

